mapper.select:
SELECT 

FROM course c
LEFT JOIN course_popularity cp ON c.id = cp.course_id


        AND c.category_id = #{categoryId}


        AND c.status = #{status}


ORDER BY c.created_at DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM course c


        AND c.category_id = #{categoryId}


        AND c.status = #{status}

mapper.insert:
INSERT INTO course (
    name, description, image, category_id, teacher_id, status,
    student_count, average_rating, created_at, updated_at
)
VALUES (
    #{name}, #{description}, #{image}, #{categoryId}, #{teacherId}, #{status},
    #{studentCount}, #{averageRating}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE course
name = #{name}, description = #{description}, image = #{image}, category_id = #{categoryId}, teacher_id = #{teacherId}, status = #{status}, student_count = #{studentCount}, average_rating = #{averageRating}, updated_at = #{updatedAt}
WHERE id = #{id}
